//Write a program which takes as input 1D- array and prints even and odd numbers in the array.
package hw2;

public class AdvancedArray7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a [] = {3,4,8,-7};
		for (int i = 0; i < a.length; i++) {
		if (a[i]%2 == 0) {
	//	System. out. print ( "Even: ");
		System. out. println ( "Even = " + a[i] );
		
	} else {
		//System. out. println ( );
		System. out. println ("Odd = " + a[i]);
	}
		}
	}
       

	}


